sysauto_script.script:
//var FiveDaysWarning = gs.daysAgo(5); //Warning after 5 days
//var TenDaysAfter = gs.daysAgo(10); //Sends notification after 10 days
var lw = 10;
var lw2 = parseInt(lw);

var fw = 5;
var fw2 = parseInt(fw);

if (lw2 > 0){
	var gr = new GlideRecord('sysevent_email_action.name'); //Points towards the incident table
	gr.addQuery('active','true');
	gr.addQuery('sys_created_on','<','TenDaysAfter');
	gr.addQuery('grupo_asignado','pointer');//
	gr.query();
	while (gr.next()){
		gr.eventQueue('source_req_item.reminder',gr);
		gr.comments = 'Mensaje que se muestra despues de 10 dias de inactividad.';
		gr.update();
	}
}

if (fw2 > 0){
	var gr = new GlideRecord('sysevent_email_action.name'); //Points towards the incident table
	gr.addQuery('active','true');
	gr.addQuery('sys_created_onn','<','TenDaysAfter');
	gr.addQuery('grupo_asignado','pointer');//
	gr.query();
	while (gr.next()){
		gr.eventQueue('source_req_item.reminder',gr);
		gr.comments = 'Mensaje que se muestra despues de 5 dias de inactividad.';
		gr.update();
	}
}

